"use strict";new Vue({el:"#main-container",data:{isQuizActive:!0,isQuizFinished:!1,current:0,score:0,quiz:hpQuiz},methods:{nextQuestion:function(){this.quiz[this.current].isAnswered&&(this.current<this.quiz.length-1?this.current+=1:this.quizFinished())},quizFinished:function(){this.isQuizActive=!1,this.isQuizFinished=!0},selectAnswer:function(e){var s=e.target,i=this.quiz[this.current];i.isAnswered||(parseInt(s.getAttribute("for"))===i.answer?s.className="correct":(s.className="incorrect",this.score+=1)),i.isAnswered=!0},finalScore:function(){return this.quiz.length-this.score+"/"+this.quiz.length}}});
